// Fonction pour générer une grille de Sudoku avec des trous (cases vides)
const generateRandomSudoku = () => {
    // Choisir une grille aléatoire parmi les grilles disponibles
    const randomIndex = Math.floor(Math.random() * sudokuGrids.length);
    let grid = JSON.parse(JSON.stringify(sudokuGrids[randomIndex]));  // Copier la grille choisie
    let emptyCells = 0; // Compteur de cases vides
    const totalEmptyCells = 40;  // Nombre de cases à rendre vides

    // Choisir des cases aléatoires à vider
    while (emptyCells < totalEmptyCells) {
        let row = Math.floor(Math.random() * 9);
        let col = Math.floor(Math.random() * 9);

        // Si la case n'est pas déjà vide (valeur = 0), on la vide
        if (grid[row][col] !== 0) {
            grid[row][col] = 0;
            emptyCells++;
        }
    }

    return grid;  // Retourner la grille modifiée avec des cases vides
};

// Fonction pour vérifier si une valeur peut être placée à une position donnée
const isValueValid = (grid, row, col, num) => {
    // Vérifier la ligne
    for (let i = 0; i < 9; i++) {
        if (grid[row][i] === num) return false;
    }
    // Vérifier la colonne
    for (let i = 0; i < 9; i++) {
        if (grid[i][col] === num) return false;
    }
    // Vérifier la sous-grille 3x3
    const startRow = Math.floor(row / 3) * 3;
    const startCol = Math.floor(col / 3) * 3;
    for (let i = 0; i < 3; i++) {
        for (let j = 0; j < 3; j++) {
            if (grid[startRow + i][startCol + j] === num) return false;
        }
    }
    return true;  // Si aucune violation n'est trouvée
};

// Fonction pour créer la grille avec des entrées interactives
const createSudokuGrid = (grid) => {
    const table = document.getElementById('sudoku-grid');
    table.innerHTML = '';  // Réinitialiser le tableau à chaque fois

    for (let row = 0; row < 9; row++) {
        const tr = document.createElement('tr');
        for (let col = 0; col < 9; col++) {
            const td = document.createElement('td');
            const value = grid[row][col];

            const input = document.createElement('input');
            input.type = 'number';
            input.min = 1;
            input.max = 9;
            input.value = value === 0 ? '' : value;

            // Désactiver les cases déjà remplies
            if (value !== 0) {
                input.disabled = true;
            } else {
                // Ajouter un gestionnaire d'événements pour valider la valeur entrée
                input.addEventListener('input', (e) => {
                    const enteredValue = parseInt(e.target.value);
                    if (isNaN(enteredValue) || !isValueValid(grid, row, col, enteredValue)) {
                        e.target.setCustomValidity("Valeur invalide dans cette case !");
                        e.target.reportValidity();
                    } else {
                        e.target.setCustomValidity(""); // Réinitialiser la validation si la valeur est valide
                        grid[row][col] = enteredValue;  // Mettre à jour la grille avec la valeur saisie
                    }
                });
            }

            td.appendChild(input);
            tr.appendChild(td);
        }
        table.appendChild(tr);
    }
};

// Fonction pour résoudre le Sudoku
const solveSudoku = (grid) => {
    const findEmptyCell = () => {
        for (let row = 0; row < 9; row++) {
            for (let col = 0; col < 9; col++) {
                if (grid[row][col] === 0) {
                    return { row, col };
                }
            }
        }
        return null;  // Si aucune case vide n'est trouvée
    };

    const isValid = (row, col, num) => {
        return isValueValid(grid, row, col, num);
    };

    const solve = () => {
        const emptyCell = findEmptyCell();
        if (!emptyCell) return true;  // Si le Sudoku est résolu

        const { row, col } = emptyCell;

        for (let num = 1; num <= 9; num++) {
            if (isValid(row, col, num)) {
                grid[row][col] = num;
                if (solve()) return true;
                grid[row][col] = 0;  // Backtrack
            }
        }

        return false;  // Si aucune solution n'est trouvée
    };

    solve();
    createSudokuGrid(grid);  // Mettre à jour l'affichage avec la solution
};

// Initialisation du Sudoku avec une grille aléatoire
let grid = generateRandomSudoku();
createSudokuGrid(grid);

